package cs2340.spacetraders.Model;import java.io.Serializable;import java.util.ArrayList;import java.util.Collections;import java.util.List;/** * Coordinates.java * Generates random coordinates for the solar systems. * * @author  Sanghavi Gaddam, Ermelinda Izihirwe, Taofikat Bishi, *          Aditya Tapshalkar, Chisomebi Obed * @version 1.0 */class Coordinates implements Serializable {    private final List<Integer> xVals = new ArrayList<>(150);    private final List<Integer> yVals = new ArrayList<>(150);    private static final float BOUND_X = 150;    private static final float BOUND_Y = 100;    /**     * Coordinates constructor - Sets random coordinates for the solar systems     */    public Coordinates() {        for (int i = 0; i < BOUND_X; i++) {            xVals.add(i);        }        for (int i = 0; i < BOUND_Y; i++) {            yVals.add(i);        }        Collections.shuffle(xVals);        Collections.shuffle(yVals);    }    /**     * getxVals method - returns the x coordinate to set for each solar system     *     * @return the x coordinate for the solar system     */    public List<Integer> getxVals() {        return Collections.unmodifiableList(xVals);    }    /**     * getyVals method - returns the y coordinate to set for each solar system     *     * @return the y coordinate for the solar system     */    public List<Integer> getyVals() {        return Collections.unmodifiableList(yVals);    }}