package cs2340.spacetraders.Model;import java.io.Serializable;import java.util.ArrayList;import java.util.Collections;/** * Coordinates.java * Generates random coordinates for the solar systems. * * @author  Sanghavi Gaddam, Ermelinda Izihirwe, Taofikat Bishi, *          Aditya Tapshalkar, Chisomebi Obed * @version 1.0 */@SuppressWarnings("SpellCheckingInspection")public class Coordinates implements Serializable {    private final ArrayList<Integer> xVals = new ArrayList<>(150);        private final ArrayList<Integer> yVals = new ArrayList<>(150);        /**     * Coordinates constructor - Sets random coordinates for the solar systems     */    public Coordinates() {        for (int i = 0; i < 150; i++) {            xVals.add(i);        }        for (int i = 0; i < 100; i++) {            yVals.add(i);        }        Collections.shuffle(xVals);        Collections.shuffle(yVals);    }        /**     * getxVals method - returns the x coordinate to set for each solar system     *     * @return the x coordinate for the solar system     */    public ArrayList<Integer> getxVals() {        return xVals;    }        /**     * getyVals method - returns the y coordinate to set for each solar system     *     * @return the y coordinate for the solar system     */    public ArrayList<Integer> getyVals() {        return yVals;    }}