package cs2340.spacetraders.Modelimport java.io.Serializableimport java.util.ArrayListimport java.util.Collections/** * Coordinates.kt * Generates random coordinates for the solar systems. * * @author  Sanghavi Gaddam, Ermelinda Izihirwe, Taofikat Bishi, * Aditya Tapshalkar, Chisomebi Obed * @version 1.0 */internal class Coordinates : Serializable {    private val xVals = ArrayList<Int>(150)    private val yVals = ArrayList<Int>(150)    /**     * Coordinates constructor - Sets random coordinates for the solar systems     */    init {        for (i in 0..149) {            xVals.add(i)        }        for (i in 0..99) {            yVals.add(i)        }        Collections.shuffle(xVals)        Collections.shuffle(yVals)    }    /**     * getxVals method - returns the x coordinate to set for each solar system     *     * @return the x coordinate for the solar system     */    fun getxVals(): ArrayList<Int> {        return xVals    }    /**     * getyVals method - returns the y coordinate to set for each solar system     *     * @return the y coordinate for the solar system     */    fun getyVals(): ArrayList<Int> {        return yVals    }}